namespace Bugo\LightPortal\Plugins\Giscus;

use Bugo\Compat\Lang;
use Bugo\Compat\Utils;
use Bugo\LightPortal\Plugins\Event;
use Bugo\LightPortal\Plugins\Plugin;
use Bugo\LightPortal\Utils\Setting;

if (! defined('LP_NAME'))
	die('No direct access...');

/**
 * Generated by PluginMaker
 */
class Giscus extends Plugin
{
	public string $type = 'comment';

	private string $url = 'https://giscus.app';

	private array $themes = [
		'light'                  => 'GitHub Light',
		'light_high_contrast'    => 'GitHub Light High Contrast',
		'light_protanopia'       => 'GitHub Light Protanopia & Deuteranopia',
		'light_tritanopia'       => 'GitHub Light Tritanopia',
		'dark'                   => 'GitHub Dark',
		'dark_high_contrast'     => 'GitHub Dark High Contrast',
		'dark_protanopia'        => 'GitHub Dark Protanopia & Deuteranopia',
		'dark_tritanopia'        => 'GitHub Dark Tritanopia',
		'dark_dimmed'            => 'GitHub Dark Dimmed',
		'preferred_color_scheme' => 'System (auto)',
		'transparent_dark'       => 'Transparent Dark',
		'noborder_light'         => 'NoBorder Light',
		'noborder_dark'          => 'NoBorder Dark',
		'noborder_gray'          => 'NoBorder Gray',
		'cobalt'                 => 'RStudio Cobalt',
		'purple_dark'            => 'Purple Dark',
		'gruvbox'                => 'Gruvbox',
		'gruvbox_dark'           => 'Gruvbox Dark',
		'gruvbox_light'          => 'Gruvbox Light',
		'catppuccin_latte'       => 'Catppuccin Latte',
		'catppuccin_frappe'      => 'Catppuccin FrappÃ©',
		'catppuccin_macchiato'   => 'Catppuccin Macchiato',
		'catppuccin_mocha'       => 'Catppuccin Mocha',
		'fro'                    => 'Fro',
	];

	public function init(): void
	{
		Lang::$txt['lp_comment_block_set'][$this->name] = 'Giscus';
	}

	public function addSettings(Event $e): void
	{
		$this->addDefaultValues([
			'theme' => 'preferred_color_scheme',
		]);

		$settings = &$e->args->settings;

		$settings[$this->name][] = [
			'text',
			'repo',
			'subtext' => sprintf($this->txt['repo_subtext'], $this->url),
			'required' => true
		];
		$settings[$this->name][] = [
			'text',
			'repo_id',
			'subtext' => sprintf($this->txt['repo_id_subtext'], $this->url),
			'required' => true
		];
		$settings[$this->name][] = [
			'text',
			'category',
			'subtext' => sprintf($this->txt['category_subtext'], $this->url),
			'required' => true
		];
		$settings[$this->name][] = [
			'text',
			'category_id',
			'subtext' => sprintf($this->txt['category_id_subtext'], $this->url),
			'required' => true
		];
		$settings[$this->name][] = ['select', 'theme', $this->themes];
	}

	public function comments(): void
	{
		if (Setting::getCommentBlock() !== $this->name)
			return;

		if (empty($this->context['repo']) || empty($this->context['repo_id']))
			return;

		if (empty($this->context['category']))
			return;

		if (empty($this->context['category_id']))
			return;

		Utils::$context['lp_giscus_comment_block'] = /** @lang text */ '
			<div class="giscus windowbg"></div>
			<script src="https://giscus.app/client.js"
				data-repo="' . $this->context['repo'] . '"
				data-repo-id="' . $this->context['repo_id'] . '"
				data-category="' . $this->context['category'] . '"
				data-category-id="' . $this->context['category_id'] . '"
				data-mapping="title"
				data-strict="1"
				data-reactions-enabled="1"
				data-emit-metadata="0"
				data-input-position="bottom"
				data-theme="' . $this->context['theme'] . '"
				data-lang="' . Lang::$txt['lang_dictionary'] . '"
				data-loading="lazy"
				crossorigin="anonymous"
				async
			>
			</script>';
	}
}
